sys_ux_client_script.script:
/** * @param {params} params * @param {api} params.api * @param {any} params.imports */
function evaluateProperty({
    api
}) {
    const loc = api.context.app.ribbonLocation;
    if (typeof loc === 'undefined') {
        loc = 'sidebar';
    }
    return (loc !== 'sidebar') || (api.context.app.ribbonLocation !== 'sidebar') || (typeof api.context.app.ribbonConfigId === 'undefined') || api.state.isLoading;
}
